import React, { useEffect, useState } from "react";
import { Card, Image } from "semantic-ui-react";

export const ViewAllItems = ({ game_id }) => {
  const [items, setItems] = useState([]);

  axios
  .get('/view_all_items', {
      params: {
          game_id
      },
  })
  .then(({ data }) => {
      const { items } = data;
      setItems(items)
  });


  // useEffect(() => {
  //   fetch("/view_all_items", { params: {game_id, id: game_id, game_id: game_id} })
  //   .then(response =>
  //     response.json().then(data => {
  //       setItems(data.items);
  //     })
  //   );
  // }, []);

  return (
    <Card.Group itemsPerRow={5}>
      {items.map(item => {
        return (
          <Card href={"/view_item_details" + item.name}>
            <Image src={item.icon} wrapped ui={false} />
            <Card.Content>
              <Card.Header>{item.name}</Card.Header>
            </Card.Content>
          </Card>
        );
      })}
    </Card.Group>
  );

};

export default ViewAllItems;
