import React, { useEffect, useState } from "react";
import { List, Header, Rating } from "semantic-ui-react";

export const ViewAllGames = () => {
  const [games, setGames] = useState([]);

  useEffect(() => {
    fetch("/games").then(response =>
      response.json().then(data => {
        setGames(data.game_id);
      })
    );
  }, []);

  return (
    <List>
      data
      {/* {games.map(game => {
        return (
          <List.Item key={game.game_id}>
            <Header>{game.game_id}</Header>
          </List.Item>
        );
      })} */}
    </List>
  );
};
